#include <iostream>
#include <functional>
#include <boost/multiprecision/cpp_int.hpp>
using namespace boost::multiprecision;

template<typename T>
T factorial(int n) { 
    std::function<T(int)> fac;
    fac = [&fac](int n){return (n < 2) ? 1 : n * fac(n-1); };
    return fac(n);
};

int main(void) {
    assert(factorial<checked_uint1024_t>(170) == checked_uint1024_t(""));
    assert(factorial<int>(11) == 39916800);
}
